import React from "react";
import {useSelector} from 'react-redux';
import {setPrevPage, setNextPage, setDefaultPage} from '../../containers/Search/actions';
import {useGetPagination} from '../../containers/Movies/hooks/useGetPagination';
import {selectTotalMovies} from '../../containers/Movies/selectors'
import Button from "../Button";


const Pagination = () => {
  const totalMovies = useSelector(selectTotalMovies);
  const {calculateItemsPerPage, checkTotalMoviesOnPage, checkIfFirstPage} = useGetPagination();

  return <div className="homepage__pagination-info">
    <p className="homepage__pagination-items">
      Items per page: 10
    </p>
    <div className="homepage__pagination-group">
      <Button
          className="homepage__pagination-prev"
          disabled={checkIfFirstPage()}
          title={calculateItemsPerPage()}
          onClick={() => setPrevPage()}
      />

      <Button
          className="homepage__pagination-next"
          disabled={checkTotalMoviesOnPage()}
          title={totalMovies}
          onClick={() => setNextPage()}
      />
    </div>
  </div>
};

export default Pagination;
