import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import './styles.css';

const SearchDropdownItem = ({ movie: { Title, Type, Poster, Year, imdbID } }) => {
    return(
        <div className="searchItem">
            <div className="searchItem__imgWrap">
                    <img className="searchItem__image" src={Poster} alt={Title} />
            </div>
            <div className="searchItem__details">
                <div className="live-search-item__group">
                    <h2 className="live-search-item__label live-search-item__option">
                        <span>Title:</span>
                        <Link to='/' className="searchItem__title">
                            {Title}
                        </Link>
                    </h2>
                    <div>
                        <span>Type:</span>
                        <strong>{Type}</strong>
                    </div>
                    <div>
                        <span>Year:</span>
                        <strong>{Year}</strong>
                    </div>
                </div>
            </div>
        </div>
    )
}

SearchDropdownItem.propTypes = {
    movie: PropTypes.shape({
        Title: PropTypes.string,
        Type: PropTypes.string,
        Poster: PropTypes.string,
        Year: PropTypes.string,
        imdbID: PropTypes.string,
    }).isRequired,
};

export default SearchDropdownItem;
